    private void largestButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_largestButtonActionPerformed
     double number1 = Double.parseDouble(largestnumber1.getText());  
     double number2 = Double.parseDouble(largestnumber2.getText());
     labelDisplay1.setText("The largest number is " + Math.max(number1,number2));
     
       
    
        
    }//GEN-LAST:event_largestButtonActionPerformed

    private void smallestButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_smallestButtonActionPerformed
     double number3 = Double.parseDouble(smallestnumber3.getText());  
     double number4 = Double.parseDouble(smallestnumber4.getText());
     labelDisplay2.setText("The smallest number is " + Math.min(number3,number4));   
    }//GEN-LAST:event_smallestButtonActionPerformed

