    private void hypotenuseButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hypotenuseButtonActionPerformed
     double number6 = Double.parseDouble(sideNumber6.getText());  
     double number7 = Double.parseDouble(sideNumber7.getText());
     labelDisplay4.setText("The hypotenuse is " + Math.hypot(number6,number7));   
    }//GEN-LAST:event_hypotenuseButtonActionPerformed

    private void rounddownbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rounddownbuttonActionPerformed
      double number8 = Double.parseDouble(numberround8.getText());  
     labelDisplay5.setText("The number rounded down is " + Math.floor(number8));     
    }//GEN-LAST:event_rounddownbuttonActionPerformed

    private void squarerootbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_squarerootbuttonActionPerformed
         double number10 = Double.parseDouble(squareroot10.getText());  
     labelDisplay7.setText("The number's squareroot is" +  Math.sqrt(number10));
    }//GEN-LAST:event_squarerootbuttonActionPerformed

    private void powerButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_powerButtonActionPerformed
     double number5 = Double.parseDouble(powerDisplay5.getText());  
     double number6 = Double.parseDouble(powernumber.getText());
     labelDisplay3.setText("The number is" +  Math.pow(number5, number6));
    }//GEN-LAST:event_powerButtonActionPerformed

    private void roundupbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_roundupbuttonActionPerformed
    double number9 = Double.parseDouble(roundup9.getText());  
     labelDisplay6.setText("The number rounded up is" +  Math.ceil(number9));
    }//GEN-LAST:event_roundupbuttonActionPerformed

    private void radiansbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_radiansbuttonActionPerformed
     double number11 = Double.parseDouble(radians11.getText());  
     labelDisplay10.setText("The number in radians is" +  Math.toRadians(number11));  
    }//GEN-LAST:event_radiansbuttonActionPerformed

